import React from 'react';
import { toast } from "react-toastify";
import Swal from 'sweetalert2';
import axios from "axios";
const CardMovieDetail = (props) => {
    const delete_item = async (id) => {

        Swal.fire({
          title: "muon xoa thiet ha?",
          text: "thiet hong dzo?",
          icon: "warning",
          showCancelButton: true,
          confirmButtonText: "oke toi luon",
          cancelButtonText: "Hủy"
        })
          .then(result => {
            if (result.isConfirmed) {
              let callAPI = "http://127.0.0.1:8000/api/movie_ID/" + id.toString()
              try {
                const data = axios({
                  method: "DELETE",
                  url: callAPI
                }).then(res => {
                  props.refetchData()
                  
                  toast.success("Xong roofi nha em yeu")
                })
    
              }
              catch (e) {
                console.log(e)
              }
    
            }
          })
      }
    const {dt}= props
    return (
        <div>
            <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 t-xs whitespace-no-wrap p-4">
                    {dt.id}
                  </td>
                  <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 t-xs whitespace-no-wrap p-4">
                    {dt.Movie}
                  </td>
                  <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 t-xs whitespace-no-wrap p-4">
                    <div className="flex">
                      {dt.Year}
                    </div>
                  </td>
                  <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 t-xs whitespace-no-wrap p-4">
                    <div className="flex">
                      {dt.Image}
                    </div>
                  </td>
                  <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 t-xs whitespace-no-wrap p-4">

                    <button type="button" className="btn btn-info" style={{marginRight:"5px"}} >EDIT</button>
                    <button type="button" className="btn btn-danger" onClick={() => delete_item(dt.id)} >DELETE</button>
                  </td>
        </div>
    );
};

export default CardMovieDetail;
