import React, { useEffect, useState } from "react";

// components

import CardTable from "components/Cards/CardTable.js";
import Axios from "axios";
import BootstrapTable from 'react-bootstrap-table-next';
import paginationFactory from 'react-bootstrap-table2-paginator';
import { profile } from "reducers/profile";
const Tables = () => {
  function list_table(listProfile) {
    var result = [];
    var i;
    for (i = 0; i < listProfile.length; i++) {
      var id = listProfile[i].id;
      var username = listProfile[i].user.username;
      var fistname = listProfile[i].fistname;
      var lastname = listProfile[i].lastname;
      var phonenum = listProfile[i].phonenum;
      var address = listProfile[i].address;
      var email = listProfile[i].user.email;
      var l = {
        "id": listProfile[i].id,
        "user": username,
        "fistname": fistname,
        "lastname": lastname,
        "phonenum": phonenum,
        "address": address,
        "email": email

      };
      // console.log(l)
      result.push(l);
    }
    return (result)
  }
  var listtable;
  const [listProfile, setlistProfile] = useState([])
  const [index, setindex] = useState()
  const getlistProfile = async (index) => {
    console.log("index",index);
    try {
      const data = await Axios.get(

        "http://127.0.0.1:8000/api/auth/profile/" + index
      );
      // console.log(typeof(data.data));
      setlistProfile(list_table(data.data));
    }
    catch (e) {
      console.log(e)
    }
  }
  function next_id(){
    var x= index+1;
    setindex(x)
  }
  useEffect(() => {

    getlistProfile(1)

  }, []);
  useEffect(() => {
    console.log("manhpro")
    getlistProfile(index)

  }, index);

  const [columns, setColumns]= useState([
    "Id",
    "User Name",
    "First Name",
    "Last Name",
    "Phone number",
    "Address" ,
    "Email"])

  return (
    <>
      <div className="flex flex-wrap mt-4">
        <div className="w-full mb-12 px-4">
          <CardTable column = {columns} data= {listProfile}/>
          <button onClick={next_id}>Next</button>
        </div>

      </div>
    </>
  );
}
export default Tables;
