import PropTypes from "prop-types";
import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import { useDispatch, useSelector } from 'react-redux'
import { userAction } from "../../action";
import axios from "axios";
import { Button, Modal } from 'react-bootstrap'
import { toast } from "react-toastify";
import Swal from 'sweetalert2';
// components
// const delete_item = async (id) => {
//   let callAPI = "http://127.0.0.1:8000/api/movie/" + id.toString()
//   console.log(callAPI)
//   try {
//     const data = await axios.delete(
//       callAPI
//     );

//   }
//   catch (e) {
//     console.log(e)
//   }
// }

export default function CardTable(props) {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const { color, column, data } = props;
  const delete_item =  async (id) => {

    Swal.fire({
        title: "muon xoa thiet ha?",
        text: "thiet hong dzo?",
        icon: "warning",
        showCancelButton: true,
        confirmButtonText: "oke toi luon",
        cancelButtonText: "Hủy"
    })
        .then(result => {
            if (result.isConfirmed) {
              let callAPI = "http://127.0.0.1:8000/api/movie_ID/" + id.toString()
              try {
                const data = axios({
                  method: "DELETE",
                  url: callAPI
                }).then(res=>{
                  props.refetchData()
                 
                  toast.success("Xong roofi nha em yeu")
                })
                
              }
              catch (e) {
                console.log(e)
              }
              
            }
        })
  }
  return (
    <>
    
      <div
        className={
          "relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded " +
          (color === "light" ? "bg-white" : "bg-blue-900 t-white")
        }
      >
        <div style={{placeSelf:"flex-end"}}>
        <button type="button" className="btn btn-dark mt-3 mr-3" >Create New Film</button>
        </div>
        
        <div className="rounded-t mb-0 px-4 py-3 border-0">
          <div className="flex flex-wrap items-center">
            <div className="relative w-full px-4 max-w-full flex-grow flex-1">
              <h3
                className={
                  "font-semibold t-lg " +
                  (color === "light" ? "t-gray-800" : "t-white")
                }
              >
                Movie Tables
              </h3>
            </div>
          </div>
        </div>
        <div className="block w-full overflow-x-auto">
          {/* Projects table */}
          <table className="items-center w-full bg-transparent border-collapse">
            <thead>
              <tr>
                {column.map(cot => (<th key={cot}
                  className={
                    "px-6 align-middle border border-solid py-3 t-xs uppercase border-l-0 border-r-0 whitespace-no-wrap font-semibold t-left " +
                    (color === "light"
                      ? "bg-gray-100 t-gray-600 border-gray-200"
                      : "bg-blue-800 t-blue-300 border-blue-700")
                  }>
                  {cot}
                </th>))}
                <th className={
                  "px-6 align-middle border border-solid py-3 t-xs uppercase border-l-0 border-r-0 whitespace-no-wrap font-semibold t-left " +
                  (color === "light"
                    ? "bg-gray-100 t-gray-600 border-gray-200"
                    : "bg-blue-800 t-blue-300 border-blue-700")
                }>Action</th>
              </tr>
            </thead>
            <tbody>
              {data.map(dt => (
                <tr key={dt.id}>
                  <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 t-xs whitespace-no-wrap p-4">
                    {dt.id}
                  </td>
                  <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 t-xs whitespace-no-wrap p-4">
                    {dt.Movie}
                  </td>
                  <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 t-xs whitespace-no-wrap p-4">
                    <div className="flex">
                      {dt.Year}
                    </div>
                  </td>
                  <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 t-xs whitespace-no-wrap p-4">
                    <div className="flex">
                      {dt.Image}
                    </div>
                  </td>
                  <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 t-xs whitespace-no-wrap p-4">

                    <Button variant="primary" style={{ marginRight: "5px" }} onClick={handleShow}>  Edit   </Button>

                    <Modal size="lg" show={show} onHide={handleClose}>
                      <Modal.Header closeButton>
                        <Modal.Title>Edit Movie</Modal.Title>
                      </Modal.Header>
                      <Modal.Body>
                        <div>
                          <form>
                            <div className="form-group">
                              <label >Movie</label>
                              <input type="text" className="form-control" aria-describedby="emailHelp" value={dt.username} />
                              
                            </div>
                            <div className="form-group">
                              <label >Year</label>
                              <input type="text" className="form-control" aria-describedby="emailHelp" placeholder="Enter email" value={dt.email} />
                              
                            </div>
                            <div className="form-group">
                              <label >Content</label>
                              <input type="text" className="form-control" aria-describedby="emailHelp" placeholder="Enter first name" />
                              
                            </div>
                            <div className="form-group">
                              <label >image</label>
                              <input type="file" className="form-control"  placeholder="Enter last name" />
                             
                            </div>
                            
                            <button type="submit" className="btn btn-primary">Submit</button>
                          </form>
                        </div>

                      </Modal.Body>
                      <Modal.Footer>
                        <Button variant="secondary" onClick={handleClose}>
                          Close
                        </Button>
                        <Button variant="primary" onClick={handleClose}>
                          Save It!
                        </Button>
                      </Modal.Footer>
                    </Modal>
                    <button type="button" className="btn btn-danger" onClick={() => delete_item(dt.id)} >DELETE</button>
                    
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <div>
        <button type="button" className="btn btn-primary" data-toggle="modal" data-target="#exampleModalLong">
          Launch demo modal
        </button>
        {/* Modal */}
        <div className="modal fade" id="exampleModalLong" tabIndex={-1} role="dialog" aria-labelledby="exampleModalLongTitle" aria-hidden="true">
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title" id="exampleModalLongTitle">Modal title</h5>
                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                  <span aria-hidden="true">×</span>
                </button>
              </div>
