import React from 'react';
import { toast } from "react-toastify";
import Swal from 'sweetalert2';
import axios from "axios";
const CardMovieDetail = (props) => {
    const [image, setimage] = useState()
    function handleChange(e){
      
      const {name,value} = e.target;
      setnewmovie(newmovie=>({...newmovie,[name]:value}));
      console.log(newmovie)
    }
    async  function handleSubmit(e){
        console.log("kjhgfds");
        e.preventDefault();
        try {
          const formData = new FormData();
    
          formData.append("Image",image);
          formData.append("Movie", newmovie['Movie']);
          formData.append("Content", newmovie['Content']);
          formData.append("Year", newmovie['Year']);
          // I just removed the curly brackets from formData
          const response = await axios.post("http://127.0.0.1:8000/api/new_movie", formData);
          props.refetchData()
          toast.success("update thanh cong")
          
    } catch (err) {
          console.error(err.message);
    }
      }
    const delete_item = async (id) => {

        Swal.fire({
            title: "muon xoa thiet ha?",
            text: "thiet hong dzo?",
            icon: "warning",
            showCancelButton: true,
            confirmButtonText: "oke toi luon",
            cancelButtonText: "Há»§y"
        })
            .then(result => {
                if (result.isConfirmed) {
                    let callAPI = "http://127.0.0.1:8000/api/movie_ID/" + id.toString()
                    try {
                        const data = axios({
                            method: "DELETE",
                            url: callAPI
                        }).then(res => {
                            props.refetchData()

                            toast.success("Xong roofi nha em yeu")
                        })

                    }
                    catch (e) {
                        console.log(e)
                    }

                }
            })
    }
    const { data } = props
    return (
        <>
            <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 t-xs whitespace-no-wrap p-4">
                {data.id}
            </td>
            <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 t-xs whitespace-no-wrap p-4">
                {data.Movie}
            </td>
            <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 t-xs whitespace-no-wrap p-4">
                <div className="flex">
                    {data.Year}
                </div>
            </td>
            <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 t-xs whitespace-no-wrap p-4">
                <div className="flex">
                    {data.Image}
                </div>
            </td>
            <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 t-xs whitespace-no-wrap p-4">


                <button type="button" className="btn btn-dark mt-3 mr-3" data-toggle="modal" data-target="#exampleModalLong" >ADD movie</button>
                <div className="modal fade" id="exampleModalLong" tabIndex={-1} role="dialog" aria-labelledby="exampleModalLongTitle" aria-hidden="true">
                    <div className="modal-dialog" role="document">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title" id="exampleModalLongTitle">Modal title</h5>
                                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                    <span aria-hidden="true">Ã</span>
                                </button>
                            </div>
                            <div className="modal-body">
                                <form name="form" >
                                    <div className="form-group">
                                        <label >Movie</label>
                                        <input type="text" className="form-control" name="Movie" value={newmovie['Movie']} onChange={handleChange} />

                                    </div>
                                    <div className="form-group">
                                        <label >Year</label>
                                        <input type="text" className="form-control" name="Year" value={newmovie['Year']} onChange={handleChange} />

                                    </div>
                                    <div className="form-group">
                                        <label >Content</label>
                                        <input type="text" className="form-control" name="Content" value={newmovie['Content']} onChange={handleChange} />

                                    </div>
                                    <div className="form-group">
                                        <label >image</label>
                                        <input type="file" className="form-control" name="Image" onChange={(e) => setimage(e.target.files[0])}
                                        />

                                    </div>


                                </form>
                            </div>
                            <div className="modal-footer">
                                <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                                <button type="button" className="btn btn-primary" onClick={handleSubmit}>Send</button>
                            </div>
                        </div>
                    </div>
                </div>
                <button type="button" className="btn btn-danger" onClick={() => delete_item(data.id)} >DELETE</button>
            </td>
        </>
    );
};

export default CardMovieDetail;
